//INPUT
//how many pairs sum 3
// generate 10 random integers numbers [-10,10]
//

var array = [];
var random = 0;
var cont = 0;
//Funcion para generar numeros aleatorios
function genNumbers(){
  return Math.round(Math.random()*10)
  }
//funcion para generar positivos y negativos aleatoriamente
function sgn(){
  let s = Math.round(Math.random());
    if(s === 0)
      return 1;
    else
      return -1;
}
//codigo de la actividad
for(let i=0; i<10; i++){
  random = genNumbers()*sgn();
  array.push(random);
}
  for(let j = 0; j<array.length-1; j++){
  if(array[j+1] + array[j] === 3){
    cont++
    console.log(j+1)
  }
}
  
    
console.log(array);
console.log(cont);


