//INPUT
//how many triplets sum 3
// generate 20 random integers numbers [-10,10]
//
var array = [];
var random = 0;
var cont = 0;

function genNumbers(){
  return Math.round(Math.random()*10)
  }

function sgn(){
  let s = Math.round(Math.random());
    if(s === 0)
      return 1;
    else
      return -1;
}

for(let i=0; i<10; i++){
  random = genNumbers()*sgn();
  array.push(random);
}
  for(let j = 0; j<array.length-2; j++){
  if(array[j+2] + array[j+1] + array[j] === 3){
    cont++
    console.log(j+2)
  }
}
  
    
console.log(array);
console.log(cont);



