// generar 1000 aleatorios y el segundo debe ser mayor al anterior, encontrar cual de esos insectos vive mas
// 1 numero --> 0-10000
// tiempo de vida del insecto 0-10000

let array = [];
let diff;
let aux = 0; 
let insect; 
let num1; 
let num2;

for(let i = 0; i<10; i++){ 
  num1 = Math.floor(Math.random()*100+1);
  num2 = Math.floor(Math.random()*100+num1);
  diff = num2-num1;
  array[i] = ([num1, num2, diff]);
  
  if(diff>aux){
    aux = diff; 
    insect = array[i];
  }
}
console.log(array);
console.log('La diferencia mayor es', aux);
console.log('Le corresponde a ', insect);