import {Queue} from '../../../jrhp/queue/queue.js'

let queue1 = new Queue();
let queue2 = new Queue();
let inversions = 0;

let valMax = 100000;


for (let i = 0; i < 1000; i++) {
  queue1.add(Math.floor(Math.random() * 1000 + 1));
}
inversions++;
console.log('queue1 (desordenada)',queue1)

while (!queue1.isEmpty()) {
  let minElement = valMax;
  let originalSize = queue1.lenght();
  inversions++;
  
  for (let i = 0; i < originalSize; i++) {
    let actual = queue1.remove();
    
    if (actual < minElement) {
      minElement = actual;
      inversions++; 
    }
    queue1.add(actual);
  }
  for (let i = 0; i < originalSize; i++) {
    let actual = queue1.remove();
    if (actual === minElement) {
      queue2.add(actual);
    } else {
      queue1.add(actual);
    }
    inversions++
  }
}



console.time();
console.log("queue2 (ordenado): ", queue2);
console.log("Pasos ejecutados: " + inversions);
console.timeEnd();


