export let isWeighted = (g) => {
  for (let i=0; i<g.length; i++)
    for (let j=0; j<g.length; j++)
      if (g[i][j] > 1){
        return true
      }
    return false
  }
export let isSymmetric = (g) => {
    for (let i=1; i<g.length; i++)
    for (let j=0; j<i; j++)
      if (g[i][j] !== g[j][i]){
        return false        
      }
    return true;
  }
 export let isCuadrada = (g) =>{
    for(let i = 0; i<=g.length;i++){
      if(g[i].length==g.length)
        return true;
      return false;
    }
  }
export let isGrafo =(g) => {
  if((isSymmetric(g))&&(isCuadrada(g)))
    return true;
  else
    return false; 
}
