console.time();

class Node {
  constructor(value) {
    this.value = value;
    this.left = null;
    this.right = null;
  }
}
class BinaryTree {
  constructor() {
    this.root = null;
  }

  createNode(value) {
    return new Node(value);
  }

  insert(value) {
    const newNode = this.createNode(value);

    if (!this.root) {
      this.root = newNode;
      return this;
    }

    let current = this.root;

    while (true) {
      if (value === current.value) return undefined;

      if (value < current.value) {
        inversions++
        if (!current.left) {
          current.left = newNode;
          return this;
        }
        current = current.left;
      } else {
        inversions++
        if (!current.right) {
          current.right = newNode;
          return this;
        }
        current = current.right;
      }
    }
  }

  traverse(fn) {
    function inOrder(node) {
      if (node) {
        inOrder(node.left);
        fn(node);
        inOrder(node.right);
      }
    }

    inOrder(this.root);
  }
}

const tree = new BinaryTree();
let inversions = 0;

for (let i = 0; i < 10000; i++) {
  tree.insert(Math.floor(Math.random() * 10000 + 1));
}
console.log(tree)
tree.traverse((node) => console.log(node.value));
console.log(`pasos: ${inversions}`)
console.timeEnd();
